
public class Vd {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		int c = -1;
		boolean b = false;
		System.out.println("So am :" + (-a));
		System.out.println("So duong :" + (+c));
		System.out.println("trai nguoc : " + (!b));
		System.out.println("tang gia tri truoc sao do thuc thi : " + ++a);
		System.out.println("thuc thi truoc sao do tang gia tri : " + a++);
		
		System.out.println("giam gia tri truoc sao do thuc thi : " + --a);
		System.out.println("thuc thi truoc sao do giam gia tri : " + a--);
		
		
	}
}
